/* eslint-disable jsx-a11y/anchor-has-content */
import React from "react";

const AboutUs = () => {
  return (
    <section
      className="hero is-medium is-bold"
      style={{
        height: "100%",
        background: "linear-gradient(to right, #fdfbfb, #ebedee)",
        padding: "2rem 0",
      }}
      id="about"
    >
      <div
        className="hero-body columns is-vcentered"
        style={{ maxWidth: "1200px", margin: "0 auto", padding: "2rem" }}
      >
        {/* Left Image Section */}
        <div
          className="column is-6"
          style={{
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          <a
            href="https://scodein.tech/"
            target="_blank"
            rel="noopener noreferrer"
            style={{ textDecoration: "none" }}
          >
            <img
              src="https://i.ibb.co/2qK1ZSc/intro.png"
              alt="student mantra"
              style={{
                width: "90%",
                maxWidth: "500px",
                borderRadius: "15px",
                boxShadow: "0 8px 20px rgba(0, 0, 0, 0.15)",
                transition: "transform 0.3s ease, box-shadow 0.3s ease",
                cursor: "pointer",
              }}
              onMouseOver={(e) => {
                e.target.style.transform = "scale(1.05)";
                e.target.style.boxShadow = "0 10px 25px rgba(0, 0, 0, 0.25)";
              }}
              onMouseOut={(e) => {
                e.target.style.transform = "scale(1)";
                e.target.style.boxShadow = "0 8px 20px rgba(0, 0, 0, 0.15)";
              }}
            />
          </a>
        </div>

        {/* Right Content Section */}
        <div className="column is-6" style={{ paddingLeft: "2rem" }}>
          <div className="container">
            <h1
              className="title"
              style={{
                fontSize: "2.5rem",
                color: "#333",
                lineHeight: "1.5",
                fontWeight: "bold",
              }}
            >
              Wonder what/why we are? üòé
            </h1>
            <h2
              className="subtitle has-text-weight-semibold"
              style={{
                fontSize: "1.2rem",
                color: "#555",
                marginTop: "1rem",
              }}
            >
              <ul style={{ listStyleType: "none", paddingLeft: "0" }}>
                <li
                  style={{
                    display: "flex",
                    alignItems: "center",
                    marginBottom: "10px",
                  }}
                >
                  <span
                    style={{
                      fontSize: "1.5rem",
                      marginRight: "10px",
                      color: "#007bff",
                    }}
                  >
                    üè¢
                  </span>
                  Scodex is a product of SCI (Student Code In)
                </li>
                <li
                  style={{
                    display: "flex",
                    alignItems: "center",
                    marginBottom: "10px",
                  }}
                >
                  <span
                    style={{
                      fontSize: "1.5rem",
                      marginRight: "10px",
                      color: "#28a745",
                    }}
                  >
                    üéì
                  </span>
                  We aim to provide information about popular Campus Ambassador
                  programs, Scholarships, and Job Opportunities.
                </li>
                <li
                  style={{
                    display: "flex",
                    alignItems: "center",
                    marginBottom: "10px",
                  }}
                >
                  <span
                    style={{
                      fontSize: "1.5rem",
                      marginRight: "10px",
                      color: "#ff5722",
                    }}
                  >
                    üåü
                  </span>
                  Our goal is to help every developer and enthusiastic learner
                  become leaders and achievers.
                </li>
              </ul>
            </h2>
            <a
              href="https://scodein.tech/"
              target="_blank"
              rel="noopener noreferrer"
              className="button is-primary is-rounded"
              style={{
                marginTop: "20px",
                padding: "10px 20px",
                fontSize: "1rem",
                backgroundColor: "#007bff",
                borderColor: "#007bff",
              }}
            >
              Visit Us
            </a>
          </div>
        </div>
      </div>
    </section>
  );
};

export default AboutUs;
