import React, { useState } from "react";

const Maintile = () => {
  const [showDetails, setShowDetails] = useState(false);

  const handleDetailsToggle = () => {
    setShowDetails(!showDetails);
  };

  return (
    <div className="section" style={{ backgroundColor: "#f9f9f9" }}>
      {/* Hero Section */}
      <div
        className="hero-body columns is-vcentered"
        style={{
          paddingBottom: "50px",
          marginTop: "80px",
        }}
      >
        <div className="column is-6" style={{ marginLeft: "50px" }}>
          <div className="container">
            <h1
              className="title"
              style={{
                fontSize: "2.5rem",
                color: "#333",
                lineHeight: "1.5",
                cursor: "pointer",
              }}
              onMouseOver={(e) =>
                (e.target.style.color = "#007bff")
              } // Hover effect
              onMouseOut={(e) => (e.target.style.color = "#333")}
            >
              Hola Amigo! &nbsp; ðŸ‘‹
            </h1>
            <h2
              className="subtitle has-text-weight-semibold"
              style={{ fontSize: "1.25rem", color: "#555", marginTop: "20px" }}
            >
              <p>
                If you are looking for a place where you can get information
                about:
              </p>

              <ul
                style={{
                  listStyleType: "none",
                  paddingLeft: "0",
                  marginTop: "20px",
                }}
              >
                <li
                  style={{
                    transition: "transform 0.3s ease",
                    cursor: "pointer",
                  }}
                  onMouseOver={(e) =>
                    (e.target.style.transform = "scale(1.05)")
                  }
                  onMouseOut={(e) =>
                    (e.target.style.transform = "scale(1)")
                  }
                >
                  âž¤ Campus Ambassador Programs
                </li>
                <li
                  style={{
                    transition: "transform 0.3s ease",
                    cursor: "pointer",
                  }}
                  onMouseOver={(e) =>
                    (e.target.style.transform = "scale(1.05)")
                  }
                  onMouseOut={(e) =>
                    (e.target.style.transform = "scale(1)")
                  }
                >
                  âž¤ Active Scholarships
                </li>
                <li
                  style={{
                    transition: "transform 0.3s ease",
                    cursor: "pointer",
                  }}
                  onMouseOver={(e) =>
                    (e.target.style.transform = "scale(1.05)")
                  }
                  onMouseOut={(e) =>
                    (e.target.style.transform = "scale(1)")
                  }
                >
                  âž¤ Full-Time Opportunities
                </li>
                <li
                  style={{
                    transition: "transform 0.3s ease",
                    cursor: "pointer",
                  }}
                  onMouseOver={(e) =>
                    (e.target.style.transform = "scale(1.05)")
                  }
                  onMouseOut={(e) =>
                    (e.target.style.transform = "scale(1)")
                  }
                >
                  âž¤ Intern Openings
                </li>
              </ul>
            </h2>
            <button
              className="button is-primary"
              style={{ marginTop: "20px" }}
              onClick={handleDetailsToggle}
            >
              {showDetails ? "Hide Details" : "Learn More"}
            </button>
          </div>
        </div>

        {/* Image Section */}
        <div className="column is-6">
          <center>
            <img
              src="https://i.ibb.co/2qK1ZSc/intro.png"
              alt="student mantra"
              width="80%"
              style={{
                borderRadius: "15px",
                transition: "transform 0.3s ease",
                cursor: "pointer",
              }}
              onMouseOver={(e) =>
                (e.target.style.transform = "scale(1.1)")
              }
              onMouseOut={(e) => (e.target.style.transform = "scale(1)")}
            />
          </center>
        </div>
      </div>

      {/* Additional Information */}
      {showDetails && (
        <div
          className="box"
          style={{
            backgroundColor: "#fff",
            padding: "20px",
            borderRadius: "10px",
            marginTop: "30px",
            transition: "opacity 0.5s ease",
          }}
        >
          <p className="has-text-centered">
            <b>
              Whichever listing you are looking for, we have a dedicated section
              for it! Explore our platform to find exactly what you need. ðŸš€
            </b>
          </p>
        </div>
      )}
    </div>
  );
};

export default Maintile;
